list-style: none;
}
%thead {
font-variant: small-caps;
}
%tbody {
font-size: larger;
}
%tfoot {
background-repeat: no-repeat;
}
%colgroup {
background-attachment: scroll;
}
%col {
background-position: center;
}
%th {
vertical-align: middle;
}
%td {
hyphens: auto;
}

